import React from 'react';
import styled from 'styled-components'

const Post = props => {
    return (
        <Container>
            {props.mode === "light" ?
                <Light>
                    <Img src={props.data.url} alt="post"></Img>
                    <Title>{props.data.title}</Title>
                </Light>:
                <Dark>
                    <Img src={props.data.url} alt="post"></Img>
                    <Title>{props.data.title}</Title>
                </Dark>
            }
        </Container>
    );
}

const Container = styled.div`
    &:hover{
        transform: scale(1.03);
        box-shadow: 0 19px 38px rgba(0,0,0,0.20), 0 15px 12px rgba(0,0,0,0.21)
    }
    transition: 0.5s;
    @media (min-width: 600px) {
        width: 40%;
        margin: 5%;
    }
    @media (min-width: 768px) {
        width: 31%;
        margin: 1%;
    }
    @media (min-width: 992px) {
        width: 23%;
        margin: 1%;
    }
    box-shadow: 0 19px 38px rgba(0,0,0,0.10), 0 15px 12px rgba(0,0,0,0.11)
`

const Light = styled.div`
    background-color: white;
    height: 100%;
`

const Dark = styled.div`
    background-color: #212121s;
    height: 100%;
`

const Img = styled.img`
    width: 100%;
`

const Title = styled.h1`
    font-family: 'Magra', sans-serif;
    padding: 5%;
    font-size: 1.2em;
`

export default Post;
