import React, { useState, useEffect } from 'react';
import Feed from './Components/Feed'
import axios from "axios";
import './App.css';

const App = props => {

    const [fetching, setFetching] = useState(false)
    const [data, setData] = useState([])
    const [limit, setLimit] = useState(0)

    const fetchData = () => {
        let url = `https://jsonplaceholder.typicode.com/photos?_start=0&_limit=30`
        axios.get(url).then(res => {
            setData(res.data)
        })
    }

    const isScrolling = () => {
        if(window.innerHeight + document.documentElement.scrollTop !== document.documentElement.offsetHeight){
          return;
        }
        setFetching(true)
    }

    const moreData = () => {
        let url = `https://jsonplaceholder.typicode.com/photos?_start=30&_limit=60`
        axios.get(url).then(res => {
            setData(old => [...old, ...res.data]);
            setFetching(false)
        })
    }

    useEffect(() => {
        if(fetching){
            setTimeout(function(){ 
                moreData()
            }, 3000);
        }
    }, [fetching]);

    useEffect(() => {
        fetchData()
        window.addEventListener("scroll", isScrolling);
        return () => window.removeEventListener("scroll", isScrolling);
    })

    return (
        <div className="App">
            <Feed data={data}/>
            {fetching ? <h1>Loading more...</h1> : null}
        </div>
    );
}

export default App;
