import React, { useState, useEffect } from 'react';
import Feed from './Components/Feed'
import axios from "axios";
import styled from 'styled-components'
import './App.css';

const App = props => {

    const [data, setData] = useState([])
    const [limit, setLimit] = useState(0)
    const [mode, setMode] = useState('light')

    const fetchData = () => {
        let url = 'https://jsonplaceholder.typicode.com/photos?_start=0&_limit='+(limit+30)
        axios.get(url).then(res => {
            setData(res.data)
        })
    }

    const isScrolling = () => {
        if(window.innerHeight + document.documentElement.scrollTop !== document.documentElement.offsetHeight){
          return;
        }
        setLimit(limit + 30)
    }

    useEffect(() => {
        fetchData()
        window.addEventListener("scroll", isScrolling);
        return () => window.removeEventListener("scroll", isScrolling);
    })

    const getContainer = () => {
        if(mode === 'light'){
            return(
                <LightContainer>
                    <h1>Light Mode</h1>
                    <button onClick={() => setMode('dark')}>Switch to Dark Mode</button>
                    <Feed mode="light" data={data}/>
                </LightContainer>
            )
        }else{
            return(
                <DarkContainer>
                    <h1>Dark Mode</h1>
                    <button onClick={() => setMode('light')}>Switch to Light Mode</button>
                    <Feed mode="dark" data={data}/>
                </DarkContainer>
            )
        }
    }

    return (
        getContainer()
    );
}

const LightContainer = styled.div`
    background-color: '#F9F8F6';
    display: flex;
    flex-direction: column;
    align-items: center;
`

const DarkContainer = styled.div`
    background-color: #121212;
    display: flex;
    flex-direction: column;
    align-items: center;
    color: white;
`

export default App;
