import React, { useState, useEffect } from 'react';
import Feed from './Components/Feed'
import axios from "axios";
import styled from 'styled-components'
import './App.css';

const App = props => {

    const [data, setData] = useState([])
    const [limit, setLimit] = useState(0)
    const [mode, setMode] = useState('light')

    const fetchData = () => {
        let url = 'https://jsonplaceholder.typicode.com/photos?_start=0&_limit='+(limit+30)
        axios.get(url).then(res => {
            setData(res.data)
        })
    }

    const isScrolling = () => {
        if(window.innerHeight + document.documentElement.scrollTop !== document.documentElement.offsetHeight){
          return;
        }
        setLimit(limit + 30)
    }

    useEffect(() => {
        fetchData()
        window.addEventListener("scroll", isScrolling);
        return () => window.removeEventListener("scroll", isScrolling);
    })

    return (
        <div>
            {mode === "light" ?
                <LightContainer>
                    <Header>
                        <h1>Blog Clone</h1>
                    </Header>
                    <Buttons>
                        <button>Dark Mode</button>
                    </Buttons>
                    {/* <button onClick={() => setMode('dark')}>Switch to Dark Mode</button> */}
                    <Feed mode="light" data={data}/>
                </LightContainer> 
                :
                <DarkContainer>
                    <button onClick={() => setMode('light')}>Switch to Light Mode</button>
                    <Feed mode="dark" data={data}/>
                </DarkContainer>
            }
        </div>
    );
}

const LightContainer = styled.div`
    background-color: #F9F8F6;
    display: flex;
    flex-direction: column;
    align-items: center;
    z-index: 0;
`

const DarkContainer = styled.div`
    background-color: #121212;
    display: flex;
    flex-direction: column;
    align-items: center;
    color: white;
    z-index: 0;
`

const Header = styled.div`
    height: 150px;
    width: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    font-family: 'PT Sans', sans-serif;
    font-size: 1.5em;
`

const Buttons = styled.div`
    button{
        border: 2px solid rgb(0,0,0,0.5);
        border-radius: 15px;
        padding: 15px;
        background: none;
    }
    position: -webkit-sticky;
    position: sticky;
    top: 0;
    z-index: 1;
    height: 150px;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: space-evenly;
    background-color: inherit;
`

export default App;
