//     flex-direction: column;
//     align-items: center;
//     justify-content: center;
//     font-family: 'PT Sans', sans-serif;
//     font-size: 1.5em;
// `

const Buttons = styled.div`
    button{
        &:hover{
            opacity: 1;
            cursor: pointer;
        }
        transition: 0.5s;
        opacity: 0.5;
        font-size: 1.05em;
        font-family: 'Pt Sans', serif;
        padding: 15px;
        background: none;
        margin-left: 20px;
        border: none;
    }
    input{
        border: none;
        font-family: 'Pt Sans', serif;
        padding: 15px;
        background: none;
    }
    position: -webkit-sticky;
    position: sticky;
    top: 0;
    z-index: 1;
    height: 100px;
    width: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: inherit;
`

export default App;
