import React, { useState, useEffect } from 'react';
import Feed from './Components/Feed'
import axios from "axios";
import './App.css';

const App = props => {

    const [fetching, setFetching] = useState(false)
    const [data, setData] = useState([])
    const [limit, setLimit] = useState(0)

    const fetchData = () => {
        let url = `https://jsonplaceholder.typicode.com/photos?_start=0&_limit=30`
        axios.get(url).then(res => {
            setData(res.data)
        })
    }

    const isScrolling = () => {
        if(window.innerHeight + document.documentElement.scrollTop !== document.documentElement.offsetHeight){
          return;
        }
        setFetching(true)
    }

    const moreData = () => {
        setLimit(limit + 30)
        let url = `https://jsonplaceholder.typicode.com/photos?_start=` + limit + `&_limit=` + (limit+30)
        axios.get(url).then(res => {
            setData([res.data]);
        })
        setFetching(false)
    }

    useEffect(() => {
        fetchData()
        window.addEventListener("scroll", isScrolling);
        return () => window.removeEventListener("scroll", isScrolling);
    })

    useEffect(() => {
        if (fetching){
          moreData();
        }
    }, [fetching]);

    return (
        <div className="App">
            <Feed data={data}/>:
        </div>
    );
}

export default App;
